import './Index.css'
import React, { useState } from 'react'
import ProjetoUm from './ProjetoUm'
import ProjetoDois from './ProjetoDois'
import ProjetoTres from './ProjetoTres'

export default function Portfolio(){

    const [slide, setSlide] = useState(1)

    const backProject = () =>{
        if(slide === 1){
            setSlide(3)
        }
        else{
            setSlide(slide-1)
        }
    }

    const nextProject = () =>{
        if(slide === 3){
            setSlide(1)
        }
        else{
            setSlide(slide+1)
        }
    }


    if(slide === 1){
        return(
            <section id='portfolio' className='portfolio'>

                <div className='arrow-to-left'onClick={()=>backProject()}><h1>&lt;</h1></div>

                <ProjetoUm/>

                <div className='arrow-to-right' onClick={()=>nextProject()}><h1>&gt;</h1></div>
            </section>
        )
    }
    else if(slide === 2){
        return(
            <section id='portfolio' className='portfolio'>

                <div className='arrow-to-left'onClick={()=>backProject()}><h1>&lt;</h1></div>

                <ProjetoDois/>

                <div className='arrow-to-right' onClick={()=>nextProject()}><h1>&gt;</h1></div>
            </section>
        )
    }

    else if(slide === 3){
        return(
            <section id='portfolio' className='portfolio'>

                <div className='arrow-to-left'onClick={()=>backProject()}><h1>&lt;</h1></div>

                <ProjetoTres/>

                <div className='arrow-to-right' onClick={()=>nextProject()}><h1>&gt;</h1></div>
            </section>
        )
    }
}
