import './Index.css'
import React, { useState } from 'react'
import ProjetoUm from './ProjetoUm'
import ProjetoDois from './ProjetoDois'
import ProjetoTres from './ProjetoTres'
import ProjetoQuatro from './ProjetoQuatro'
import ProjetoCinco from './ProjetoCinco'
import ProjetoSeis from './ProjetoSeis'
import ProjetoSete from './ProjetoSete'
import ProjetoOito from './ProjetoOito'

export default function Portfolio(){

    const [slide, setSlide] = useState(1)

    const backProject = () =>{
        if(slide === 1){
            setSlide(8)
        }
        else{
            setSlide(slide-1)
        }
    }

    const nextProject = () =>{
        if(slide === 8){
            setSlide(1)
        }
        else{
            setSlide(slide+1)
        }
    }


    if(slide === 1){
        return(
            <section id='portfolio' className='portfolio'>

                <div className='arrow-to-left'onClick={()=>backProject()}><h1>&lt;</h1></div>

                <ProjetoUm/>

                <div className='arrow-to-right' onClick={()=>nextProject()}><h1>&gt;</h1></div>
            </section>
        )
    }
    else if(slide === 2){
        return(
            <section id='portfolio' className='portfolio'>

                <div className='arrow-to-left'onClick={()=>backProject()}><h1>&lt;</h1></div>

                <ProjetoDois/>

                <div className='arrow-to-right' onClick={()=>nextProject()}><h1>&gt;</h1></div>
            </section>
        )
    }

    else if(slide === 3){
        return(
            <section id='portfolio' className='portfolio'>

                <div className='arrow-to-left'onClick={()=>backProject()}><h1>&lt;</h1></div>

                <ProjetoTres/>

                <div className='arrow-to-right' onClick={()=>nextProject()}><h1>&gt;</h1></div>
            </section>
        )
    }

    else if(slide === 4){
        return(
            <section id='portfolio' className='portfolio'>

                <div className='arrow-to-left'onClick={()=>backProject()}><h1>&lt;</h1></div>

                <ProjetoQuatro/>

                <div className='arrow-to-right' onClick={()=>nextProject()}><h1>&gt;</h1></div>
            </section>
        )
    }

    else if(slide === 5){
        return(
            <section id='portfolio' className='portfolio'>

                <div className='arrow-to-left'onClick={()=>backProject()}><h1>&lt;</h1></div>

                <ProjetoCinco/>

                <div className='arrow-to-right' onClick={()=>nextProject()}><h1>&gt;</h1></div>
            </section>
        )
    }

    else if(slide === 6){
        return(
            <section id='portfolio' className='portfolio'>

                <div className='arrow-to-left'onClick={()=>backProject()}><h1>&lt;</h1></div>

                <ProjetoSeis/>

                <div className='arrow-to-right' onClick={()=>nextProject()}><h1>&gt;</h1></div>
            </section>
        )
    }

    else if(slide === 7){
        return(
            <section id='portfolio' className='portfolio'>

                <div className='arrow-to-left'onClick={()=>backProject()}><h1>&lt;</h1></div>

                <ProjetoSete/>

                <div className='arrow-to-right' onClick={()=>nextProject()}><h1>&gt;</h1></div>
            </section>
        )
    }

    else if(slide === 8){
        return(
            <section id='portfolio' className='portfolio'>

                <div className='arrow-to-left'onClick={()=>backProject()}><h1>&lt;</h1></div>

                <ProjetoOito/>

                <div className='arrow-to-right' onClick={()=>nextProject()}><h1>&gt;</h1></div>
            </section>
        )
    }
}
