import './Index.css'
import React, { useState } from 'react'
import PortfolioUm from './PortfolioUm'
import PortfolioDois from './PortfolioDois'
import PortfolioTres from './PortfolioTres'

export default function Portfolio(){

    const [slide, setSlide] = useState(1)

    const backPortfolio = () =>{
        if(slide === 1){
            setSlide(3)
        }
        else{
            setSlide(slide-1)
        }
    }

    const nextPortfolio = () =>{
        if(slide === 3){
            setSlide(1)
        }
        else{
            setSlide(slide+1)
        }
    }


    if(slide === 1){
        return(
            <section id='portfolio' className='portfolio'>

                <div className='arrow-to-left'onClick={()=>backPortfolio()}><h1>&lt;</h1></div>

                <PortfolioUm/>

                <div className='arrow-to-right' onClick={()=>nextPortfolio()}><h1>&gt;</h1></div>
            </section>
        )
    }
    else if(slide === 2){
        return(
            <section id='portfolio' className='portfolio'>

                <div className='arrow-to-left'onClick={()=>backPortfolio()}><h1>&lt;</h1></div>

                <PortfolioDois/>

                <div className='arrow-to-right' onClick={()=>nextPortfolio()}><h1>&gt;</h1></div>
            </section>
        )
    }

    else if(slide === 3){
        return(
            <section id='portfolio' className='portfolio'>

                <div className='arrow-to-left'onClick={()=>backPortfolio()}><h1>&lt;</h1></div>

                <PortfolioTres/>

                <div className='arrow-to-right' onClick={()=>nextPortfolio()}><h1>&gt;</h1></div>
            </section>
        )
    }
}
