/* eslint-disable react/jsx-no-target-blank */
/* eslint-disable jsx-a11y/alt-text */
/* eslint-disable array-callback-return */
import './Index.css'
import React, { useEffect, useState } from 'react'
import Projetos from './Projetos.js'


export default function Portfolio(){

    const [slide, setSlide] = useState(1)
    const [imagem, setImagem] = useState()
    const [titulo, setTitulo] = useState()
    const [texto, setTexto] = useState()
    const [link, setLink] = useState()
    
    const lenObeject = Projetos.length

    const backProject = () =>{
        if(slide === 1){
            setSlide(lenObeject)
            
            let container = document.getElementById('container-portfolio')
            container.animate([
                // keyframes
                { opacity: '0' },
                { opacity: '1' }
              ], {
                // timing options
                duration: 2000
              });
        }
        else{
            setSlide(slide-1)

            let container = document.getElementById('container-portfolio')
            container.animate([
                // keyframes
                { opacity: '0' },
                { opacity: '1' }
              ], {
                // timing options
                duration: 2000
              });
        }
    }

    const nextProject = () =>{
        if(slide === lenObeject){
            setSlide(1)

            let container = document.getElementById('container-portfolio')
            container.animate([
                // keyframes
                { opacity: '0' },
                { opacity: '1' }
              ], {
                // timing options
                duration: 2000
              });
        }
        else{
            setSlide(slide+1)

            let container = document.getElementById('container-portfolio')
            container.animate([
                // keyframes
                { opacity: '0' },
                { opacity: '1' }
              ], {
                // timing options
                duration: 2000
              });
        }
    }

    useEffect(()=>{
        Projetos.map((projeto, i)=>{
            let ind = i+1
            if(slide === ind){
                setImagem(projeto.imagem)
                setTitulo(projeto.titulo)
                setTexto(projeto.descricao)
                setLink(projeto.url)
            }
        })

    },[slide])

    return(

        <section id='portfolio' className='portfolio'>

            <div className='arrow-to-left'onClick={()=>backProject()}><h1>&lt;</h1></div>

            <div id='container-portfolio' className='container-portfolio'>
                <div className='div-project'>
                    <div className='block-image-project'>
                        <img className='image-project' src={imagem}/>
                    </div>
                    <div className='block-text-project'>
                        <h3>{titulo}</h3>
                        <p>{texto}</p>
                    </div>
                </div>
                <div className='button-saiba-mais'>
                    <a href={link} target='_blank'><button className='saiba-mais-project'>Saiba mais</button></a>
                </div>
            </div>

            <div className='arrow-to-right' onClick={()=>nextProject()}><h1>&gt;</h1></div>
        </section>

    )
        
}
